sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;
	c.data.list = true;
	c.data.edit = false;

	$rootScope.$on("selectNote",function(event,data){
		c.server.get({
			action:"getNote",
			noteID:$rootScope.noteID
		}).then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});

	c.updateForm = function(){
		var noteUpdate = {'title': c.data.title,'note':c.data.note,'sys_id':c.data.noteID};
		c.server.get({
			action:"updateNote",
			updateObject:noteUpdate
		}).then(function(r){
		});
	}

	c.editForm = function(){

		if(c.data.list) 
			c.data.list = false;
		else 
			c.data.list = true;

		if(c.data.edit) 
			c.data.edit = false; 
		else 
			c.data.edit = true;
	};
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input){
		var noteGR = new GlideRecord("x_snc_createnotes_note");
		if(noteGR.get(input.noteID) && input.action == "getNote"){
			/*
		data.note.title = noteGR.getValue("title");
		data.note.note = noteGR.getValue("number");
		data.note.noteID = noteGR.getValue("sys_id");*/
			data.note = {};
			$sp.getRecordValues(data.note,noteGR,"title, note, sys_id");
		}else if(input.action == "updateNote"){
			var grNoteUpdate = new GlideRecord("x_snc_createnotes_note")
			var obj = input.updateObject;
			if(grNoteUpdate.get(obj.sys_id)){
				grNoteUpdate.setValue("title",obj.title);
				grNoteUpdate.setValue("note",obj.note);
				grNoteUpdate.update();
			}			 
		}
	}
})();
