sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;

	c.shareNote = function(index){
		var selected_note_sysID = index

		$rootScope.noteID = selected_note_sysID;
		$rootScope.$emit("selectNote",selected_note_sysID);
	}
};

sp_widget.script:
(function() {

	//create an array to populate with notes
	data.notes = [];
	var noteObj = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {

		noteObj.push({'id':noteGR.getValue("sys_id").toString(),'number':noteGR.getValue("number").toString(),'title':noteGR.getValue("title")});
	}
	data.notes = noteObj;
})();
